usethis::use_testthat()
test_that("correct result for univariate gaussian", {
  expect_equal(mvnpdf(x=matrix(1.96), Log=FALSE)$y, dnorm(1.96))
  expect_equal(mvnpdf(x=matrix(c(1.96, -0.5), ncol = 2), Log=FALSE)$y,
               dnorm(c(1.96, -0.5)))
})

test_that("correct results for bivariate gaussian", {
  expect_equal(mvnpdf(x=matrix(rep(1.96,2), nrow=2, ncol=1), Log=FALSE)$y,
               mvtnorm::dmvnorm(rep(1.96, 2)))
})

library(microbenchmark)
library(future.apply)

log_seq <- function(x){
  # try this yourself (spoiler alert: it is quite long...):
  # res <- numeric(length(x))
  # for(i in 1:length(x)){
  #   res[i] <- log(x[i])
  # }
  # return(res)
  return(log(x))
}

log_par <- function(x){
  res <- future_sapply(1:length(x), FUN = function(i) {
    log(x[i])
  })
  return(res)
}

plan(multisession(workers = 3))
mb <- microbenchmark(log_par(1:100), log_seq(1:100), times = 50)

x <- matrix(rnorm(1e6), nrow = 10)
dim(x)## [1]     10 100000
colmedian_apply <- function(x){
  return(apply(x, 2, median))
}
system.time(colmedian_apply(x))##    user  system elapsed 
##   1.551   0.005   1.559
colmedian_for <- function(x){
  ans <- rep(0, ncol(x)) 
  for (i in 1:ncol(x)) {
    ans[i] <- median(x[, i]) 
  }
  return(ans)
}
system.time(colmedian_for(x))##    user  system elapsed 
##   1.486   0.003   1.492
colmedian_par <- function(x){
  res <- future_sapply(1:ncol(x), FUN = function(i) {
          median(x[, i])
    })
  return(res)
}
plan(multisession(workers = 3))
system.time(colmedian_par(x))##    user  system elapsed 
##   0.097   0.016   0.712
colmedian_parIter <- function(x, ncores = 1){
  iter <- itertools::isplitIndices(n = ncol(x), chunks = ncores)
  res <- future_sapply(iter, FUN = function(i) {
          apply(x[, i], 2, median)
    })
  return(unlist(res))
}
system.time(colmedian_parIter(x, ncores = 3))##    user  system elapsed 
##   0.048   0.014   0.540
mb <- microbenchmark(colmedian_apply(x), 
                     colmedian_for(x),
                     colmedian_par(x),
                     colmedian_parIter(x, ncores = 3), times = 10)
